function submitMPSearch(){var d=document.getElementById("bloggerPricingPageId").innerHTML.trim();var g=document.getElementById("keyword").value.trim();if(g.length>60){$("#mp-error").text("Keyword cannot have more than 60 characters.");return false}var c=document.getElementById("country").value;var k=document.getElementById("tag").value.trim();if(k.length>60){$("#mp-error").text("Skill cannot have more than 60 characters.");return false}var b=document.getElementById("ddFrom").value.trim();if(b.length>0){if(!(b%1===0)){$("#mp-error").text("Delivery Days must be a number.");return false}}var h=document.getElementById("ddTo").value.trim();if(h.length>0){if(!(h%1===0)){$("#mp-error").text("Delivery Days must be a number.");return false}}var j=document.getElementById("priceFrom").value.trim();if(j.length>0){if(!(j%1===0)){$("#mp-error").text("Price must be a number.");return false}}var i=document.getElementById("priceTo").value.trim();if(i.length>0){if(!(i%1===0)){$("#mp-error").text("Price must be a number.");return false}}var a=document.createElement("form");a.setAttribute("method","POST");a.setAttribute("action","/user/marketplace");a.setAttribute("target","_self");var e=document.createElement("input");e.setAttribute("name","pageId");e.setAttribute("value",d);a.appendChild(e);var e=document.createElement("input");e.setAttribute("name","keyword");e.setAttribute("value",g);a.appendChild(e);var e=document.createElement("input");e.setAttribute("name","country");e.setAttribute("value",c);a.appendChild(e);var e=document.createElement("input");e.setAttribute("name","tag");e.setAttribute("value",k);a.appendChild(e);var e=document.createElement("input");e.setAttribute("name","ddFrom");e.setAttribute("value",b);a.appendChild(e);var e=document.createElement("input");e.setAttribute("name","ddTo");e.setAttribute("value",h);a.appendChild(e);var e=document.createElement("input");e.setAttribute("name","priceFrom");e.setAttribute("value",j);a.appendChild(e);var e=document.createElement("input");e.setAttribute("name","priceTo");e.setAttribute("value",i);a.appendChild(e);var e=document.createElement("input");e.setAttribute("name","sortCriteria");e.setAttribute("value",document.getElementById("sortCriteria").value);a.appendChild(e);document.body.appendChild(a);a.submit()}function showOrderMPPricing(a){if(userIdHidden.value=="NULL"){$("body, #promptSignupBox, #modal-background").toggleClass("active");return false}$("body, #orderBloggerPricingBox, #modal-background").toggleClass("active");$("#bloggerPricingBloggerId").html(MP[a]["bloggerId"]);$("#bloggerPricingPostType").html(MP[a]["postType"]);$("#bloggerPricingDeliveryDays").html(MP[a]["deliveryDays"]);$("#bloggerPricingPrice").html(MP[a]["price"])}function orderBloggerPricing(){if(userIdHidden.value=="NULL"){$("body, #promptSignupBox, #modal-background").toggleClass("active");return false}var a=$("#orderBloggerPricingRetMessage").html();if(a!="Please wait..."){$("#orderBloggerPricingRetMessage").html("Please wait...");$.ajax({type:"POST",url:"/action/order-blogger-pricing",data:{pageId:document.getElementById("bloggerPricingPageId").innerHTML.trim(),bloggerId:document.getElementById("bloggerPricingBloggerId").innerHTML.trim(),postType:document.getElementById("bloggerPricingPostType").innerHTML.trim(),deliveryDays:document.getElementById("bloggerPricingDeliveryDays").innerHTML.trim(),price:document.getElementById("bloggerPricingPrice").innerHTML.trim()},dataType:"json"}).always(function(b){$.each(b,function(c,d){switch(c){case"ERROR":$("#orderBloggerPricingRetMessage").html(d);break;case"SUCCESS":$("#orderBloggerPricingRetMessage").html(d);break;case"orderHandler":window.location.href="/user/order-payment/"+d;break}})})}};