function subscribe(){if(document.getElementById("emailAddress").value.trim().length==0){$("#subscribeError").text("Enter your email address.")}else{var a=subscribeButton.innerHTML;if(a=="Please wait..."){return false}else{document.getElementById("subscribeButton").href=function(){return false};subscribeButton.innerHTML="Please wait..."}$.ajax({type:"POST",url:"/action/subscribe",data:{username:document.getElementById("emailAddress").value.trim()},dataType:"text"}).always(function(b){if(b!="SUCCESS"){$("#subscribeError").text(b)}subscribeButton.innerHTML="SUBSCRIBE";document.getElementById("subscribeButton").href=function(){javascript:subscribe()}})}};