function unfollowAllComments(){var a=unfollowAllCommentsRetMessage.innerHTML;if(a=="Please wait..."){return false}else{unfollowAllCommentsRetMessage.innerHTML="Please wait..."}if(confirm("Are you sure you want to Unfollow Comments on All Posts? This action is irriversible.")){$.post("/user/unfollow-all-comments").always(function(b){if(b=="SUCCESS"){followCommentsCount.innerHTML="0";unfollowAllCommentsRetMessage.innerHTML="You have been unsubscribed from the comments on All Posts."}else{unfollowAllCommentsRetMessage.innerHTML=b}})}else{unfollowAllCommentsRetMessage.innerHTML=""}}function saveNotifications(){var f=saveNotifRetMessage.innerHTML;if(f=="Saving..."){return false}else{saveNotifRetMessage.innerHTML="Saving..."}var e="S";if(document.getElementById("followWhenPublished").value!=document.getElementById("originalFollowWhenPublished").value){e=document.getElementById("followWhenPublished").value}var a="S";if(document.getElementById("followWhenCommented").value!=document.getElementById("originalFollowWhenCommented").value){a=document.getElementById("followWhenCommented").value}var d="S";if(document.getElementById("notifyWhenThanked").value!=document.getElementById("originalNotifyWhenThanked").value){d=document.getElementById("notifyWhenThanked").value}var b="S";if(document.getElementById("remindDraftPost").value!=document.getElementById("originalRemindDraftPost").value){b=document.getElementById("remindDraftPost").value}var c="S";if(document.getElementById("weeklyNewsLetter").value!=document.getElementById("originalWeeklyNewsLetter").value){c=document.getElementById("weeklyNewsLetter").value}if(e=="S"&&a=="S"&&d=="S"&&b=="S"&&c=="S"){saveNotifRetMessage.innerHTML="No changes to save.";return false}$.post("/user/save-notification-flags",{followWhenPublished:e,followWhenCommented:a,notifyWhenThanked:d,remindDraftPost:b,weeklyNewsLetter:c}).always(function(g){if(g=="SUCCESS"){originalFollowWhenPublished.value=document.getElementById("followWhenPublished").value;originalFollowWhenCommented.value=document.getElementById("followWhenCommented").value;originalNotifyWhenThanked.value=document.getElementById("notifyWhenThanked").value;originalRemindDraftPost.value=document.getElementById("remindDraftPost").value;originalWeeklyNewsLetter.value=document.getElementById("weeklyNewsLetter").value;saveNotifRetMessage.innerHTML="Your preferences have been successfully saved."}else{saveNotifRetMessage.innerHTML=g}})};