function postComment(){if(document.getElementById("textComment").value.trim().length==0){$("#commentsError").text("Please enter your comment.");return false}var a=$("#commentsError").html();if(a!="Please wait..."){$("#commentsError").html("Please wait...");$.ajax({type:"POST",url:"/action/order-post-comment",data:{orderId:document.getElementById("orderIdHidden").value.trim(),bloggerId:document.getElementById("bloggerIdHidden").value.trim(),buyerId:document.getElementById("buyerIdHidden").value.trim(),commentText:document.getElementById("textComment").value.trim()},dataType:"json"}).always(function(b){$.each(b,function(c,d){switch(c){case"ERROR":$("#commentsError").text(d);break;case"SUCCESS":$("#commentsError").text(d);$("#orderProgress").append('<div style="margin-top:8px; font-weight:bold; color:#707070">just now - '+userIdHidden.value+' commented.</div><div style="padding:3px 10px 3px 6px"><pre style="white-space:pre-wrap;">'+document.getElementById("textComment").value.trim()+"</pre></div>");$("#textComment").val("");break}})})}}function reviewOrder(){var a=$("#commentsError").html();if(a!="Please wait..."){$("#commentsError").html("Please wait...");$.ajax({type:"POST",url:"/action/order-review",data:{orderId:document.getElementById("orderIdHidden").value.trim(),buyerId:document.getElementById("buyerIdHidden").value.trim()},dataType:"json"}).always(function(b){$.each(b,function(c,d){switch(c){case"ERROR":$("#commentsError").text(d);break;case"SUCCESS":$("#commentsError").text(d);break}})})}}function closeOrder(){var a=$("#commentsError").html();if(a!="Please wait..."){$("#commentsError").html("Please wait...");$.ajax({type:"POST",url:"/action/order-close",data:{orderId:document.getElementById("orderIdHidden").value,bloggerId:document.getElementById("bloggerIdHidden").value,price:document.getElementById("priceHidden").value},dataType:"json"}).always(function(b){$.each(b,function(c,d){switch(c){case"ERROR":$("#commentsError").text(d);break;case"SUCCESS":window.location.href="/user/orders";break}})})}}function cancelOrder(){var a=$("#commentsError").html();if(a!="Please wait..."){$("#commentsError").html("Please wait...");$.ajax({type:"POST",url:"/action/order-cancel",data:{orderId:document.getElementById("orderIdHidden").value,bloggerId:document.getElementById("bloggerIdHidden").value,buyerId:document.getElementById("buyerIdHidden").value,price:document.getElementById("priceHidden").value,stripeChargeId:document.getElementById("stripeChargeIdHidden").value},dataType:"json"}).always(function(b){$.each(b,function(c,d){switch(c){case"ERROR":$("#commentsError").text(d);break;case"SUCCESS":window.location.href="/user/orders";break}})})}};