function cancelPayment(){var a=$("#page-payment-error").html();if(a!="Please wait..."){$("#page-payment-error").html("Please wait...");var c=document.getElementById("paymentHandlerHidden").value.trim();$.ajax({type:"POST",url:"/action/page-payment-delete",data:{paymentHandler:c},dataType:"text"}).always(function(b){if(b=="NULL"){window.location.href="/user/pages"}else{window.location.href="/user/page-payments/"+b}})}};