function applyCoupon(){$("#page-payment-error").html("");var a=document.getElementById("page-payment-coupon").value.trim();if(a=="NC19"&&document.getElementById("basic-monthly").checked){$("#page-payment-amount").html("19");$("#page-payment-error").html("Coupon has been successfully applied.")}else{if(a=="NC190"&&document.getElementById("basic-yearly").checked){$("#page-payment-amount").html("190");$("#page-payment-error").html("Coupon has been successfully applied.")}else{if(a=="NC99"&&document.getElementById("premium-monthly").checked){$("#page-payment-amount").html("99");$("#page-payment-error").html("Coupon has been successfully applied.")}else{if(a=="NC199"&&document.getElementById("premium-monthly").checked){$("#page-payment-amount").html("199");$("#page-payment-error").html("Coupon has been successfully applied.")}else{$("#page-payment-error").html("This coupon is not applicable for the product selected.")}}}}}function makeOneTime(){var d=$("#page-payment-error").html();if(d!="Please wait..."){$("#page-payment-error").html("Please wait...");var f=document.getElementById("pageIdHidden").value.trim();var g=$("input[type=radio][name=product-type]:checked").attr("id");var e=document.getElementById("page-payment-amount").innerHTML;var h=document.getElementById("page-payment-coupon").value.trim();$.ajax({type:"POST",url:"/action/make-one-time-page-payment",data:{pageId:f,productType:g,amount:e,coupon:h},dataType:"json"}).always(function(a){$("#page-payment-error").html("");$.each(a,function(b,c){switch(b){case"ERROR":$("#page-payment-error").html(c);break;case"paymentHandler":window.location.href="/user/page-payment/"+c;break}})})}}function setupRecurring(){var d=$("#page-payment-error").html();if(d!="Please wait..."){$("#page-payment-error").html("Please wait...");var f=document.getElementById("pageIdHidden").value.trim();var g=$("input[type=radio][name=product-type]:checked").attr("id");var e=document.getElementById("page-payment-amount").innerHTML;var h=document.getElementById("page-payment-coupon").value.trim();$.ajax({type:"POST",url:"/action/setup-recurring-page-payments",data:{pageId:f,productType:g,amount:e,coupon:h},dataType:"json"}).always(function(a){$("#page-payment-error").html("");$.each(a,function(b,c){switch(b){case"ERROR":$("#page-payment-error").html(c);break;case"paymentHandler":window.location.href="/user/page-payment/"+c;break}})})}};