function showPageBox(a){if(userIdHidden.value=="NULL"){$("body, #promptSignupBox, #modal-background").toggleClass("active");return false}$("body, #pageBox, #modal-background").toggleClass("active");$.ajax({type:"POST",url:"/user/get-page-box",data:{bloggerId:userIdHidden.value},dataType:"json"}).always(function(b){$.each(b,function(e,g){switch(e){case"ERROR":alert(g);break;case"pagesList":var f=new Array(),c=-1;PL=new Array();for(var d=0;d<g.length;d++){f[++c]='<table id="'+g[d].pageId+'" onclick="selectPage(\''+g[d].pageId+"','"+a+"')\" ondblclick=\"selectPageAndClose('"+g[d].pageId+"','"+a+'\')" style="width:400px"><tr><td>';f[++c]='</td><td style="vertical-align:top"><div style="margin-left:20px">';f[++c]='<div style="font-weight:bold; cursor:pointer">'+g[d].pageName+"</div>";f[++c]="</div></td></tr></table>";PL[d]=g[d].pageId}if(PL.length==0){$("#boxResults").html('<div style="margin-left:10px; margin-top:30px; font-size:12px">No Pages.</div>')}else{$("#boxResults").html(f.join(""));selectPage(PL[0],a)}break}})})}function selectPageAndClose(a,b){if(b=="mrl"){pageIdSelected.innerHTML=" "+a+" ";document.getElementById("pageIdRadio").checked=true}else{bloggerPricingPageId.innerHTML=" "+a+" "}$("body, #pageBox, #modal-background").toggleClass("active")}function selectPage(a,c){if(c=="mrl"){pageIdSelected.innerHTML=" "+a+" ";document.getElementById("pageIdRadio").checked=true}else{bloggerPricingPageId.innerHTML=" "+a+" "}for(var b=0;b<PL.length;b++){if(PL[b]==a){document.getElementById(PL[b]).style.backgroundColor="rgb(208, 219, 225)"}else{document.getElementById(PL[b]).style.backgroundColor="white"}}};