$(document).ready(function(){$("#paypal-submit").click(function(a){pid=document.getElementById("pageIdHidden").value})});function applyCoupon(b){$("#page-payment-error-"+b).html("");var a=document.getElementById("page-payment-coupon-"+b).value.trim();if(a=="NC100"&&document.getElementById("page-payment-type-3-"+b).checked){$("#page-payment-amount-"+b).html("100");$("#paypal-amount").value("100");$("#page-payment-error-"+b).html("Coupon has been successfully applied.")}else{$("#page-payment-error-"+b).html("This coupon is not applicable for the product selected.")}};